import React, { Component } from 'react';
import classes from './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons:[
      {id:'qqq',name:'Max',age:24},
      {id:'www',name:'Manu',age:29},
      {id:'eee',name:'Stephanie',age:19}
    ],
    otherState:'nihao',
    showPersons: false
  }

nameChangedHandler = (event,id) =>{
  const personIndex = this.state.persons.findIndex( p =>{
    return p.id === id;
  })
  const person = {
    ...this.state.persons[personIndex]
  }
  // const person = Object.assign({},this.state.persons[personIndex])

  person.name = event.target.value;

  const persons = [...this.state.persons];
  persons[personIndex] = person;

  this.setState({persons:persons})
}

deletePersonHandler = (personIndex) =>{
  //const persons = this.state.persons.slice();
  const persons = [...this.state.persons]; 
  persons.splice(personIndex,1);
  this.setState({persons:persons})
}

togglePersonHandler = () =>{
    const doesShow = this.state.showPersons;
    this.setState({showPersons:!doesShow});
}

render() {
    let persons = null;
    let btnClass = '';
    if(this.state.showPersons){
      persons=(
        <div>
        {/* <Person name={this.state.persons[0].name} age={this.state.persons[0].age}/>  
        <Person name={this.state.persons[1].name} age={this.state.persons[1].age}
                click={this.switchNameHandler.bind(this,'Wei')}
                changed = {this.nameChangedHandler}>
            My hobbies :Racing </Person>  
        <Person name={this.state.persons[2].name} age={this.state.persons[2].age}/>   */}
        {this.state.persons.map((person,index) => {
          return <Person click={()=> this.deletePersonHandler(index)} 
                  name={person.name} 
                  age={person.age}
                  key={person.id}
                  changed={(event) => this.nameChangedHandler(event, person.id)}/>
        })}
        </div>
      );

      btnClass = classes.Red;
    }

    const assignedlasses = [];
    if(this.state.persons.length <= 2){
      assignedlasses.push(classes.red);// classes = ['red']
    }
    if(this.state.persons.length <= 1){
      assignedlasses.push(classes.bold);//classes = ['bold','red']
    }

    return (
      <div className={classes.App}>
        <h1>Hi, I'm a React App</h1>
        <p className={assignedlasses.join(' ')}>This is really working!</p>
        <button className={btnClass}
                onClick={this.togglePersonHandler}>Toggle Person</button>
          {persons}
      </div>
    );
}
}

export default App;


